/*Table structure for table `base_menu` */

DROP TABLE IF EXISTS `base_menu`;

CREATE TABLE `base_menu` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `version` int(11) DEFAULT NULL COMMENT 'VERSION',
  `menu_code` varchar(32) DEFAULT NULL,
  `menu_url` varchar(255) DEFAULT NULL COMMENT '菜单对应的URL',
  `menu_name` varchar(255) DEFAULT NULL COMMENT '菜单名称',
  `menu_parent` varchar(32) DEFAULT NULL COMMENT '上级菜单ID',
  `menu_disorder` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `status` int(11) DEFAULT NULL COMMENT '=-1删除,=1有效 =0无效',
  `created_by` varchar(32) DEFAULT NULL COMMENT '创建人',
  `created_date` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `last_updated_by` varchar(32) DEFAULT NULL COMMENT '最后更新人',
  `last_updated_date` timestamp NULL DEFAULT NULL COMMENT '最后更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=104 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='后台菜单表';

